    val listData: ArrayList<Scientist> get() {
        val list = arrayListOf<Scientist>()
        for (position in scientistNames.indices) {
            val scientist = Scientist()
            scientist.id = scientistId[position]
            scientist.name = scientistNames[position]
            scientist.detail = scientistDetails[position]
            scientist.photo = scientistsImages[position]
            scientist.proffesion = scientistProffesion[position]
            scientist.era = scientistEra[position]
            scientist.interest = scientistInterest[position]
            scientist.wiki = scientistWiki[position]
            list.add(scientist)
        }
        return list
    }
